mapper.select:
SELECT B.BU_MAIN_IMG, R.ROOM_NO, R.ROOM_H_COUNT, R.ROOM_PRICE, R.ROOM_INFO, B.BU_NO, B.BU_ADDRESS, B.AREA_NO, B.BU_CATEGORY, B.BU_TEL, B.BU_TITLE, ROUND(COALESCE((SELECT AVG(STAR_SCORE) FROM REVIEW WHERE ROOM_NO = R.ROOM_NO), 3), 1) AS userStarScore FROM BUSINESS B INNER JOIN ( SELECT DISTINCT ROOM_NO, ROOM_H_COUNT, ROOM_PRICE, ROOM_INFO, BU_NO FROM ROOM ) R ON B.BU_NO = R.BU_NO WHERE B.BU_MAIN_IMG IS NOT NULL AND B.APPROVAL = 'Y' AND B.BU_CATEGORY = #{category}
AND B.AREA_NO = TO_NUMBER(#{area})
AND R.ROOM_H_COUNT = 1

SELECT B.BU_MAIN_IMG, S.ROOM_PRICE, S.ROOM_INFO, S.ROOM_H_COUNT, B.BU_ADDRESS, B.AREA_NO, B.BU_CATEGORY, B.BU_TEL, B.BU_NO, B.BU_TITLE, ROUND(COALESCE((SELECT AVG(STAR_SCORE) FROM REVIEW WHERE ROOM_NO = S.ROOM_NO), 3), 1) AS userStarScore FROM BUSINESS B INNER JOIN ( SELECT DISTINCT R.ROOM_NO, R.BU_NO, R.ROOM_H_COUNT, R.ROOM_PRICE, R.ROOM_INFO FROM ROOM R LEFT JOIN BOOKING BK ON BK.ROOM_NO = R.ROOM_NO WHERE (
(BK.STATUS = 'Y' AND (TO_DATE(BK.ROOM_CHECKOUT, 'YYYY.MM.DD') < TO_DATE(#{date_in}, 'YYYY-MM-DD')) OR TO_DATE(BK.ROOM_CHECKIN, 'YYYY.MM.DD') > TO_DATE(#{date_out}, 'YYYY-MM-DD')) OR
BK.STATUS IS NULL ) ) S ON S.BU_NO = B.BU_NO WHERE B.BU_MAIN_IMG IS NOT NULL AND B.APPROVAL = 'Y' AND B.BU_CATEGORY = #{category}
AND B.AREA_NO = TO_NUMBER(#{area})
AND S.ROOM_H_COUNT = 1
AND S.ROOM_H_COUNT = #{guests}
AND TO_NUMBER(REPLACE(S.ROOM_PRICE, ',', '')) BETWEEN TO_NUMBER(REPLACE(#{prices[0]}, '[', '')) AND TO_NUMBER(REPLACE(#{prices[1]}, ']', ''))
AND S.ROOM_INFO IN (
#{reserve}
)
AND S.ROOM_INFO IN (
#{option}
)
AND ROUND(COALESCE((SELECT AVG(STAR_SCORE) FROM REVIEW WHERE ROOM_NO = S.ROOM_NO), 3), 1) < TO_NUMBER(#{userStarScore}) + 1 AND ROUND(COALESCE((SELECT AVG(STAR_SCORE) FROM REVIEW WHERE ROOM_NO = S.ROOM_NO), 3), 1) >= TO_NUMBER(#{userStarScore})

SELECT
BU_ADDRESS,
AREA_NO,
BU_CATEGORY,
BU_TITLE
FROM HOUSE H
WHERE
MAIN_IMG IS NOT NULL AND BU_CATEGORY = #{category}

	AND AREA_NO = #{areaNo}


	 AND BU_ADDRESS LIKE #{keyword} 
	 AND AREA_NO LIKE #{keyword} 
	 AND BU_CATEGORY LIKE #{keyword} 
	 AND BU_TITLE LIKE #{keyword}

SELECT
BU_ADDRESS,
AREA_NO,
BU_CATEGORY,
BU_TITLE
FROM HOUSE H
WHERE
MAIN_IMG IS NOT NULL AND BU_CATEGORY = #{category}

   AND AREA_NO = #{areaNo}


    AND BU_ADDRESS LIKE #{keyword} 
    AND AREA_NO LIKE #{keyword} 
    AND BU_CATEGORY LIKE #{keyword} 
    AND BU_TITLE LIKE #{keyword}
