mapper.update:
UPDATE NOTICE SET
NOTICE_TITLE = #{noticeTitle},
NOTICE_CONTENT = #{noticeContent}
WHERE NOTICE_NO = #{noticeNo}

UPDATE NOTICE SET
COUNT = COUNT +1
WHERE NOTICE_NO = #{noticeNo}

UPDATE  BUSINESS SET
APPROVAL = 'Y'
WHERE BU_ID = #{buId}

UPDATE  BUSINESS SET
BU_USER_NAME = #{buUserName},
BU_TITLE = #{buTitle},
BU_ADDRESS = #{buAddress}
WHERE BU_ID = #{buId}

UPDATE  REVIEW SET
REVIEW_DECLARIATION = 'N'
WHERE REVIEW_NO = #{reviewNo}
